import CardImage from './CardImage';

require('../assets/cards/s2.png');
console.log('here');



const getCardPicture = (card) => {
  console.log(card);
  let imagePath = `../assets/cards/${card}.png`;
  return imagePath;
};

const Hand = (props) => {
  return (
    <>
      <div className="flex-container">
        {
          props.hand.map((card,i) => (
            <div key={i}>{card}
              <CardImage card={card}/>
            </div>
          ))
        }
      </div>
    </>
  );
}
